import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

// ==== IMPORT DES ICONES ====
import htmlIcon from '../assets/img/html.png';
import cssIcon from '../assets/img/css.png';
import jsIcon from '../assets/img/js.png';
import tsIcon from '../assets/img/ts.png';
import phpIcon from '../assets/img/php.png';
import reactIcon from '../assets/img/react.png';
import nextIcon from '../assets/img/nextjs.png';
import nativeIcon from '../assets/img/reactnative.png';
import expressIcon from '../assets/img/express.png';
import laravelIcon from '../assets/img/laravelle.png';
import sqlIcon from '../assets/img/sql.png';
import nosqlIcon from '../assets/img/nosql.png';
import umlIcon from '../assets/img/uml.png';
import prestashoplIcon from '../assets/img/prestashop.png';

export const Skills = () => {
  const responsive = {
    superLargeDesktop: { breakpoint: { max: 4000, min: 3000 }, items: 5 },
    desktop: { breakpoint: { max: 3000, min: 1024 }, items: 3 },
    tablet: { breakpoint: { max: 1024, min: 464 }, items: 2 },
    mobile: { breakpoint: { max: 464, min: 0 }, items: 1 }
  };

  // Liste des compétences avec icônes
  const skills = [
    { name: "HTML", category: "Languages", icon: htmlIcon },
    { name: "CSS", category: "Languages", icon: cssIcon },
    { name: "JavaScript", category: "Languages", icon: jsIcon },
    { name: "TypeScript", category: "Languages", icon: tsIcon },
    { name: "PHP", category: "Languages", icon: phpIcon },
    { name: "React.js", category: "Frontend", icon: reactIcon },
    { name: "Next.js", category: "Frontend", icon: nextIcon },
    { name: "React Native", category: "Mobile", icon: nativeIcon },
    { name: "Express.js", category: "Backend", icon: expressIcon },
    { name: "Laravel", category: "Backend", icon: laravelIcon },

    { name: "SQL", category: "Database", icon: sqlIcon },
    { name: "NoSQL", category: "Database", icon: nosqlIcon },
    { name: "UML", category: "Modeling", icon: umlIcon },
  ];

  return (
    <section className="skill" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Skills</h2>
              <p>
                I’m passionate about building modern web and mobile applications, 
                combining frontend, backend, and design knowledge to deliver complete solutions.
              </p>

              <Carousel responsive={responsive} infinite={true} className="skill-slider">
                {skills.map((skill, index) => (
                  <div key={index} className="item">
                    <img
                      src={skill.icon}
                      alt={skill.name}
                      style={{ width: "80px", height: "80px", marginBottom: "10px" }}
                    />
                    <h5>{skill.name}</h5>
                    <small>{skill.category}</small>
                  </div>
                ))}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
