import React from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import colorSharp from "../assets/img/color-sharp.png";

export const Skills = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  return (
    <section className="skill" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Skills</h2>
              <p>
                Passionate and versatile developer with experience across
                front-end, back-end, and mobile technologies. I build efficient,
                scalable, and user-friendly applications.
              </p>

              <Carousel
                responsive={responsive}
                infinite={true}
                className="owl-carousel owl-theme skill-slider"
              >
                {/* LANGUAGES */}
                <div className="item">
                  <h5>Languages</h5>
                  <p>HTML • CSS • Tailwind • Bootstrap • JavaScript • TypeScript • PHP</p>
                </div>

                {/* FRONTEND */}
                <div className="item">
                  <h5>Frontend</h5>
                  <p>React.js • Next.js</p>
                </div>

                {/* MOBILE */}
                <div className="item">
                  <h5>Mobile</h5>
                  <p>React Native</p>
                </div>

                {/* BACKEND */}
                <div className="item">
                  <h5>Backend</h5>
                  <p>Express.js • Laravel</p>
                </div>

                {/* DATABASES */}
                <div className="item">
                  <h5>Databases</h5>
                  <p>SQL • NoSQL</p>
                </div>

                {/* MODELISATION */}
                <div className="item">
                  <h5>Modeling</h5>
                  <p>UML</p>
                </div>
              </Carousel>
            </div>
          </div>
        </div>
      </div>

      <img className="background-image-left" src={colorSharp} alt="background" />
    </section>
  );
};
