import { Container, Row, Col, Tab, Nav, Modal, Button, Carousel } from "react-bootstrap";
import { useState } from "react";
import projImg1 from "../assets/img/project-img1.png";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import colorSharp2 from "../assets/img/color-sharp2.png";

export const Projects = () => {
  const [modalShow, setModalShow] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);

  const projects = [
    {
      title: "Business Startup",
      description: "Design & Development",
      images: [projImg1, projImg2, projImg3],
      github: "https://github.com/toncompte/business-startup",
      website: "https://business-startup.com",
      period: "Jan 2024 - Mar 2024"
    },
    {
      title: "Mobile App",
      description: "App Development",
      images: [projImg2, projImg3],
      github: "https://github.com/toncompte/mobile-app",
      website: "",
      period: "Apr 2024 - Jun 2024"
    },
  ];

  const openModal = (project) => {
    setSelectedProject(project);
    setModalShow(true);
  };

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <h2>Projects</h2>
            <p>
              Here are some of my personal and enterprise projects, showcasing my skills in web, mobile, and telecom development.
            </p>

            <Row>
              {projects.map((project, index) => (
                <Col md={4} key={index} className="mb-3">
                  <div 
                    className="project-card"
                    onClick={() => openModal(project)}
                    style={{ cursor: "pointer" }}
                  >
                    <img src={project.images[0]} alt={project.title} className="img-fluid" />
                    <h5 className="mt-2">{project.title}</h5>
                  </div>
                </Col>
              ))}
            </Row>

            {/* ==== Modal ==== */}
            <Modal show={modalShow} onHide={() => setModalShow(false)} size="lg" centered>
              {selectedProject && (
                <>
                  <Modal.Header closeButton>
                    <Modal.Title>{selectedProject.title}</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <Carousel>
                      {selectedProject.images.map((img, idx) => (
                        <Carousel.Item key={idx}>
                          <img className="d-block w-100" src={img} alt={`Slide ${idx}`} />
                        </Carousel.Item>
                      ))}
                    </Carousel>
                    <p className="mt-3">{selectedProject.description}</p>
                    <p><strong>Period:</strong> {selectedProject.period}</p>
                    {selectedProject.github && (
                      <p><strong>GitHub:</strong> <a href={selectedProject.github} target="_blank" rel="noreferrer">{selectedProject.github}</a></p>
                    )}
                    {selectedProject.website && (
                      <p><strong>Website:</strong> <a href={selectedProject.website} target="_blank" rel="noreferrer">{selectedProject.website}</a></p>
                    )}
                  </Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={() => setModalShow(false)}>Close</Button>
                  </Modal.Footer>
                </>
              )}
            </Modal>

          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="" />
    </section>
  );
};
