import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

export const Skills = () => {
  const responsive = {
    superLargeDesktop: { breakpoint: { max: 4000, min: 3000 }, items: 5 },
    desktop: { breakpoint: { max: 3000, min: 1024 }, items: 3 },
    tablet: { breakpoint: { max: 1024, min: 464 }, items: 2 },
    mobile: { breakpoint: { max: 464, min: 0 }, items: 1 }
  };

  // Liste des compétences avec pourcentage et catégorie
  const skills = [
    { name: "HTML / CSS", category: "Languages", level: 95 },
    { name: "JavaScript", category: "Languages", level: 90 },
    { name: "TypeScript", category: "Languages", level: 85 },
    { name: "PHP", category: "Languages", level: 80 },
    { name: "React.js", category: "Frontend", level: 92 },
    { name: "Next.js", category: "Frontend", level: 88 },
    { name: "React Native", category: "Mobile", level: 85 },
    { name: "Express.js", category: "Backend", level: 87 },
    { name: "Laravel", category: "Backend", level: 83 },
    { name: "SQL", category: "Database", level: 90 },
    { name: "NoSQL", category: "Database", level: 85 },
    { name: "UML", category: "Modélisation", level: 80 },
  ];

  return (
    <section className="skill" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Skills</h2>
              <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.<br />Lorem Ipsum has been the industry's standard dummy text.</p>

              <Carousel responsive={responsive} infinite={true} className="skill-slider">
                {skills.map((skill, index) => (
                  <div key={index} className="item">
                    <div className="skill-circle" style={{
                      background: `conic-gradient(
                        #8e44ad ${skill.level * 3.6}deg,
                        #9b59b6 0
                      )`
                    }}>
                      <span>{skill.level}%</span>
                    </div>
                    <h5>{skill.name}</h5>
                    <small>{skill.category}</small>
                  </div>
                ))}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
